import { useContext } from "react";
import{AuthContext} from "../context/AuthContext";
import { Box } from "@mui/material";
import TextField from '@mui/material/TextField';
import Button from '@mui/material/Button';


export const LoginPage = () => {
  const loginUser  = useContext(AuthContext);
  const handleSubmit = e => {
    e.preventDefault();

    const username1 = e.target.username.value;
    const password1 = e.target.password.value;

    console.log(username1+password1);
    if(username1.length > 0 )
        loginUser.loginUser(username1, password1);
    else    
        alert('enter username and password');

  };

  return (
    <Box
    component="main"
    sx={{ flexGrow: 1, p: 0.05,display:'flex'}}>
   <Paper
      style={{
        display: "grid",
        gridRowGap: "20px",
        padding: "20px",
        margin: "10px 300px",
      }}
      elevation={3}
    ></Paper>
      <form onSubmit={handleSubmit}>
        <h1>Login </h1>
        <hr />
        <span ></span>
        <TextField label="Enter Username" id="username" type="text" variant="outlined" />

        <TextField type="password" id="password" label="Enter Password"/>
        <br></br>

        <Button type="submit" variant="contained">Login</Button>

      </form>

    </Box>
  );
};
