    const request_url = url_backend + '/api/services/';

    const x = data.map((obj) => {
      return obj.actual_instances;
    })[0];
    const temp = scaledinstances - x;

    if (temp > 0) {
      if (type === 'Free Scale') {
        alert('no limitations. ' + temp + ' instances will be added');
