export const Home = () => {
  const [open, setOpen] = React.useState(false);
  const handleClickOpen = () => {
    setOpen(true);
    getAPIData();

  };

  const handleClose = () => {
    setOpen(false);
  };

  const request_url=url_backend+'/api/auditlogs/';

  const getAPIData = async () => {
    try {
      const data = await axios.get<Record<string,unknown>>(
        request_url
      );

      setAuditLogdata(JSON.stringify(data.data));
    } catch (e) {
      console.log(e);
    }
  };
  const [auditlogdata,setAuditLogdata]=React.useState('');

  return (
    <Box
      component="main"
      sx={{
        flexGrow: 0,
        p: 3,
        display: 'flex',
        alignItems: 'center',
        position: 'relative',
      }}
    >
      <Toolbar />
      <Stack direction="column">
        <Stack direction="row">
          <SettingsCard />
          <StatusCard />
          <VersionInfoCard />
        </Stack>
        <Stack direction="row">
          <NotificationsCard />
          <HealthCard />
        </Stack>

      <Stack direction="row" justifyContent="flex-end" >

      <Button size="small" variant='contained' onClick={(event) => handleClickOpen()}><BookIcon></BookIcon>View Logs</Button>

      </Stack>
      </Stack>
      <div>
      <Dialog open={open} onClose={handleClose}>
        <DialogTitle> Application Audit Logs</DialogTitle>
        <DialogContent>
          <DialogContentText sx={{whiteSpace:'pre-line',fontFamily:'monospace',flex:1,flexWrap:'wrap'}} color="red">
           {auditlogdata}
          </DialogContentText>

        </DialogContent>
        <DialogActions>
          <Button onClick={handleClose}>Close</Button>
          <Button onClick={handleClose}>Refresh</Button>
        </DialogActions>
      </Dialog>
      </div>
    </Box>

  );
};
