import { createContext, useState, useEffect,FC } from "react";
import jwt_decode from "jwt-decode";
import { useNavigate } from "react-router-dom";
import * as React from 'react';
import url_backend from "../configs/url";


export interface IAuth{
user:any,
authTokens:any,
usertoken?:string,
setUser:(usertoken:string)=>void,
setAuthTokens:(usertoken:string)=>void,
registerUser:(username,password,password2)=>void,
loginUser:(username,password)=>void,
logoutUser:()=>void

};

export const AuthContext=createContext<IAuth>({} as IAuth);
export const AuthProvider= ({ children}) => {
    const usertoken=localStorage.getItem("authTokens");
  const [authTokens, setAuthTokens] = useState(() =>usertoken!==null
      ? JSON.parse(usertoken)
      : '' 
 );
  const [user, setUser] = useState(() =>
    usertoken!==null
      ? jwt_decode(usertoken) 
      : '' 
  );
  const [loading, setLoading] = useState(true);

  const history = useNavigate();
  const url=url_backend+'/api/auth/token/';

  const loginUser = async (username, password) => {


    const response = await fetch(url, {
      method: "POST",
      headers: {
        "Content-Type": "application/json"
      },
      body: JSON.stringify({
        username,
        password
      })
    });

    const data = await response.json();


    if (response.status === 200) {
      setAuthTokens(data);
      setUser(jwt_decode(data.access));
      localStorage.setItem("authTokens", JSON.stringify(data));

    history('/app/controlcenter/')
    } else {
      alert(JSON.stringify(data));
    }
  };

  const url2=url_backend+'/api/auth/register/'

  const registerUser = async (username, password, password2) => {

    const response = await fetch(url2, {
      method: "POST",
      headers: {
        "Content-Type": "application/json"
      },
      body: JSON.stringify({
        username,
        password,
        password2
      })
    });
    if (response.status === 201) {

    history('/app/controlceter/login')
    } else {
      alert("Something is Wrong!");
    }

  };

  const logoutUser = () => {
    alert('')
    setAuthTokens(null);
    setUser(null);
    localStorage.removeItem("authTokens");

   history("/")
  };


  useEffect(() => {
    if (authTokens) {
      setUser(jwt_decode(authTokens.access));
    }
    setLoading(false);
  }, [authTokens, loading]);

  return (

    <AuthContext.Provider value={{
    user,
    setUser,
    setAuthTokens,
    authTokens,
    registerUser,
    loginUser,
    logoutUser

    }}>
      {loading ? null : children}
    </AuthContext.Provider>
  );
};
