import { Box } from "@mui/system";
import { Toolbar } from "@mui/material";
import axios from 'axios';
import * as React from 'react';
// import { HostsData } from "./HostsTable";
import { EditHosts } from "./HostsEdit";
import {useLocation} from 'react-router-dom';
import url_backend from "../configs/url";
import { usePromiseTracker } from "react-promise-tracker";
import { trackPromise } from 'react-promise-tracker';
import {ThreeDots} from 'react-loader-spinner';


export const LoadingIndicator = () => {

  const { promiseInProgress } = usePromiseTracker();

 return (

  promiseInProgress ? (
  // <h1>Hey some async call in progress ! </h1>
  <div
     style={{
        width: "100%",
        height: "100",
        display: "flex",
        justifyContent: "center",
        alignItems: "center"
      }}
    >
     <ThreeDots 
	color={'blue'} 
	height={150} 
	width={150} 
	/>
    </div>
  ):(<></>)

  );  
}

export type HostsInfoprops={
  id?: string;
}

export interface HostsData{
    _id:               string;
    name:              string;
    description:       string;
    OS:                string;
    Running_services:  number;
    Running_instances: number;
    health_status:     string;
    services:          Service[];
    labels:            string[];
    number_of_cores:   number;
    processor_type:    string;
    memory:            string;
    total_storage:     string;
    storage_mounts:    StorageMount[];
  }

  export interface Service {
    Name:      string;
    Instances: number;
  }

  export interface StorageMount {
    Mount_point: string;
    Storage:     string;
  }

export const HostsEditLanding =(props:HostsInfoprops)=>{

  const [data,setData]=React.useState<HostsData[]>([]);
  const location=useLocation();
  const idhere=location.state as HostsInfoprops;



  const getProductData = async () => {

      let url=url_backend+'/api/hosts/?name=';
      url=url+idhere.id;
      console.log(url);
      try {


        const data = await axios.get<HostsData[]>(
          url
        );

        setData(data.data);
        console.log("Data:"+{data});
      } catch (e) {

        console.log(e);
      }
    };

  React.useEffect(() => {
     trackPromise(getProductData());
  },[] );

  console.log("After use effect"+data);

  return (
    <>
    <Box
      component="main"
      sx={{ flexGrow: 1, p: 3, display:'flex'}}>

      <Toolbar />
  {/* <h1>Hello</h1> */}

    <EditHosts data={data} />

</Box>
<LoadingIndicator/>
</>

  );
};
